import './App.css';
import { useState } from 'react';



const options = [{label: "apple", value: 1, selected: false}, {label: "pear", value: 2, selected: false}, {label: "banana", value: 3, selected: false}]



function App() {


  const [isOpen, setIsOpen] = useState(false);
  const [optionsDropdown, setOptionsDropdown] = useState(options);

  const toggleOption = (option) => {
    setOptionsDropdown([...options, {...option, selected: !option.selected}])
  }

  return (
  <div className="flex-col flex items-center justify-center min-h-screen">  
    <div className="px-16 py-4 space-y-4 border flex rounded-lg" onClick={() => setIsOpen(!isOpen)}> 
      Click
    </div>
    <div>
      {isOpen && 
        <ul className="flex flex-col border gap-2">
          {optionsDropdown.map((option) => (
            <li className={`px-3 py-2 cursor-pointer hover:bg-gray-100 flex items-center gap-2 ${option.selected ? "bg-blue-50" : ""}`}
              key={option.value}
              onClick={() => toggleOption(option)}
              >
              {option.label}
            </li>
          ))}
        </ul>
      }
    </div>
  </div>
  );
}

export default App;
