import { useState, useEffect } from "react";
import { FaCaretDown, FaCaretUp } from "react-icons/fa";
import { useRef } from "react";



export const Select = ({options, isMulti}) => {
    const [isOpen, setIsOpen] = useState(false);
    const [optionsDropdown, setOptionsDropdown] = useState([{label: "Select all", value: null, selected: false}, ...options]);
    const dropdownRef = useRef(null);

    useEffect(() => {
        const handleClickOutside = (event) => {
          if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
            setIsOpen(false);
          }
        };

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
          document.removeEventListener("mousedown", handleClickOutside);
        };
      }, []);

    const selectOption = (clickedOption) => {
      if (clickedOption.value === null) {
        setOptionsDropdown(optionsDropdown.map((option) => { 
            return { ...option, selected: clickedOption.selected ? false : true }
        }));
      }
      else {
        setOptionsDropdown(optionsDropdown.map(option =>
            option.value === clickedOption.value
              ? { ...option, selected: !option.selected }
              : option
          ));
      }
    };

    // const onClear = () => {
    //     setOptionsDropdown(optionsDropdown.map((option) => { 
    //         return { ...option, selected: false }
    //     }));
    // }

    const selectedOptions = optionsDropdown
        .filter(option => option.selected && option.value != null)   
        .map(option => option.label)         
        .join(", ");

    return (
    <div className="flex-col flex w-64" ref={dropdownRef}>  
      <div className="w-full">

        <div className="z-0 px-2 py-3 border border-2 flex rounded-lg text-left items-center justify-between border-blue-500" onClick={() => setIsOpen(!isOpen)}> 
            <label className="absolute mb-10 bg-white px-1 text-xs text-blue-800">{"title"}</label>
            <span title={selectedOptions} className={`w-full whitespace-nowrap overflow-hidden overflow-ellipsis pr-2 ${selectedOptions.length === 0 ? 'text-gray-500' : ''}`}>{selectedOptions.length > 0 ? selectedOptions : 'Select...'}</span> 
            <div className="flex gap-2">
                {/* <span className="text-xs text-gray-500 z-50 cursor-pointer" onClick={(e) => {onClear();  e.stopPropagation(); }}>{"Clear"}</span>     */}
                <span className="flex">{isOpen ? <FaCaretUp color={'gray'} /> : <FaCaretDown color={'gray'} />}</span>   
            </div>        
        </div>
        <div>
          {isOpen && 
            <ul className="max-h-48 overflow-y-auto rounded-lg flex flex-col border shadow-md">
              {optionsDropdown.map((option) => (
                <div className={`px-3 py-2 cursor-pointer hover:bg-gray-100 flex items-center gap-2 ${option.selected ? "bg-blue-50" : ""}`}   onClick={() => selectOption(option)}>
                    {isMulti && option.value != null &&
                        <input
                        type="checkbox"
                        checked={option.selected}
                        readOnly 
                        />
                        }
                    <li
                      key={option.value}
                      className={"whitespace-nowrap overflow-hidden overflow-ellipsis"}
                    >
                      {option.label}
                    </li>
                  </div>
              ))}
            </ul>
          }
        </div>
      </div>
    </div>
    );
}
