import { useState } from "react";



export const Select = ({options, isMulti}) => {
    const [isOpen, setIsOpen] = useState(false);
    const [optionsDropdown, setOptionsDropdown] = useState(options);

    const toggleOption = (clickedOption) => {
      setOptionsDropdown(optionsDropdown.map(option =>
        option.value === clickedOption.value
          ? { ...option, selected: !option.selected }
          : option
      ));
    };
    return (
    <div className="flex-col flex items-center justify-center">  
      <div className="relative">
        <div className="px-24 py-3 space-y-4 border flex rounded-lg" onClick={() => setIsOpen(!isOpen)}> 
            {optionsDropdown
                .filter(option => option.selected)   
                .map(option => option.label)         
                .join(", ")                         
                || "Select items"}             
        </div>
        <div>
          {isOpen && 
            <ul className="max-h-48 overflow-y-auto rounded-lg flex flex-col border">
              {optionsDropdown.map((option) => (
                <div  className={`px-3 py-2 cursor-pointer hover:bg-gray-100 flex items-center gap-2 ${option.selected ? "bg-blue-50" : ""}`}   onClick={() => toggleOption(option)}>
                  <input
                      type="checkbox"
                      checked={option.selected}
                      readOnly 
                    />
                    <li
                      key={option.value}
                    >
                      {option.label}
                    </li>
                  </div>
              ))}
            </ul>
          }
        </div>
      </div>
    </div>
    );
}
