import './App.css';
import { useState } from 'react';



const options = [{label: "apple", value: 1}, {label: "pear", value: 2}, {label: "banana", value: 3}]
function App() {
  const [isOpen, setIsOpen] = useState(false);
  return (
  <div className="flex-col items-center">  
    <div className="p-8 space-y-4 flex items-center justify-center" onClick={() => setIsOpen(!isOpen)}> 
      Click
    </div>
    <div>
      {isOpen && 
        <div className="flex flex-col">
          {options.map((option) => (
            <span>
              {option.label}
            </span>
          ))}
        </div>
      }
    </div>
  </div>
  );
}

export default App;
