import { useState } from "react";
import { FaCaretDown, FaCaretUp } from "react-icons/fa";



export const Select = ({options, isMulti}) => {
    const [isOpen, setIsOpen] = useState(false);
    const [optionsDropdown, setOptionsDropdown] = useState(options);

    const toggleOption = (clickedOption) => {
      setOptionsDropdown(optionsDropdown.map(option =>
        option.value === clickedOption.value
          ? { ...option, selected: !option.selected }
          : option
      ));
    };

    const onClear = () => {
        setOptionsDropdown(optionsDropdown.map((option) => { 
            return { ...option, selected: false }
        }));
    }

    const selectedOptions = optionsDropdown
        .filter(option => option.selected)   
        .map(option => option.label)         
        .join(", ");

    return (
    <div className="flex-col flex w-64">  
      <div className="w-full">
        <div className="z-0 px-2 py-2 space-y-4 border flex rounded-lg text-left items-center justify-between border-blue-500" onClick={() => setIsOpen(!isOpen)}> 
            <span className={`w-full whitespace-nowrap overflow-hidden overflow-ellipsis pr-2 ${selectedOptions.length === 0 ? 'text-gray-500' : ''}`}>{selectedOptions.length > 0 ? selectedOptions : 'Select...'}</span> 
            <div className="flex items-center gap-2">
                <span className="text-xs text-gray-500 z-50" onClick={(e) => {onClear();  e.stopPropagation(); }}>{"Clear"}</span>    
                <span className="flex">{isOpen ? <FaCaretUp /> : <FaCaretDown />}</span>   
            </div>        
        </div>
        <div>
          {isOpen && 
            <ul className="max-h-48 overflow-y-auto rounded-lg flex flex-col border shadow-md">
              {optionsDropdown.map((option) => (
                <div  className={`px-3 py-2 cursor-pointer hover:bg-gray-100 flex items-center gap-2 ${option.selected ? "bg-blue-50" : ""}`}   onClick={() => toggleOption(option)}>
                    {isMulti && 
                        <input
                        type="checkbox"
                        checked={option.selected}
                        readOnly 
                        />
                        }
                    <li
                      key={option.value}
                    >
                      {option.label}
                    </li>
                  </div>
              ))}
            </ul>
          }
        </div>
      </div>
    </div>
    );
}
